import React from "react";
import BuscaPais from "./components/BuscaPais";
import ListaPokemon from "./components/ListaPokemon";
import comLogging from "./components/comLogging";
import Filtro from "./components/Filtro";
import BuscadorProdutos from "./components/BuscadorProdutos";
import "./styles.css";

/**
 * ðŸŽ¨ Componente App
 * 
 * Este Ã© o componente principal da aplicaÃ§Ã£o que demonstra
 * diferentes padrÃµes e conceitos do React.
 * 
 * ðŸ“š Componentes Demonstrados:
 * - BuscaPais: Demonstra requisiÃ§Ãµes HTTP e estados
 * - ListaPokemon: Demonstra useEffect e listas
 * - MeuComponenteComLog: Demonstra HOC
 * - Filtro: Demonstra Render Props
 * - BuscadorProdutos: Demonstra fetch e grid
 */
const App = () => {
  const tarefas = [
    { id: 1, texto: "Estudar React" },
    { id: 2, texto: "Praticar Hooks" },
    { id: 3, texto: "Criar componentes" },
    { id: 4, texto: "Implementar estilos" },
    { id: 5, texto: "Testar aplicaÃ§Ã£o" }
  ];

  const MeuComponenteComLog = comLogging(() => (
    <div className="componente-log">
      <h3>Componente com Logging</h3>
      <p>Este componente foi montado e desmontado com sucesso!</p>
    </div>
  ));

  return (
    <div className="app">
      <header className="cabecalho">
        <h1>DemonstraÃ§Ã£o de Componentes React</h1>
        <p>Exemplos interativos de diferentes padrÃµes e conceitos</p>
      </header>

      <main className="conteudo">
        <section className="secao">
          <h2>Busca de PaÃ­ses</h2>
          <p>DemonstraÃ§Ã£o de requisiÃ§Ãµes assÃ­ncronas e tratamento de erros</p>
          <BuscaPais />
        </section>

        <section className="secao">
          <h2>Lista de PokÃ©mon</h2>
          <p>DemonstraÃ§Ã£o de paginaÃ§Ã£o e exibiÃ§Ã£o de dados em grade</p>
          <ListaPokemon />
        </section>

        <section className="secao">
          <h2>Higher Order Component</h2>
          <p>DemonstraÃ§Ã£o do padrÃ£o HOC com logging</p>
          <MeuComponenteComLog />
        </section>

        <section className="secao">
          <h2>Render Props</h2>
          <p>DemonstraÃ§Ã£o do padrÃ£o Render Props com filtro</p>
          <Filtro items={tarefas}>
            {({ itemsFiltrados, filtro, setFiltro, totalItems, itemsFiltradosCount, isEmpty, isFiltered, onLimparFiltro, animacao }) => (
              <div>
                <ul className="lista-tarefas">
                  {itemsFiltrados.map(item => (
                    <li key={item.id} className={`item-tarefa ${animacao}`}>
                      {item.texto}
                    </li>
                  ))}
                </ul>
              </div>
            )}
          </Filtro>
        </section>

        <section className="secao">
          <h2>Lista de Produtos</h2>
          <p>DemonstraÃ§Ã£o de exibiÃ§Ã£o de produtos com detalhes</p>
          <BuscadorProdutos />
        </section>
      </main>
    </div>
  );
};

export default App;
